17.pageContent:
```js

let obj1 = {

title: 'Blue Nana',

city: 'Hamburg',

image: 'https://i.imgur.com/Sd1AgUOm.jpg',

};

let obj2 = {

name: 'Niki de Saint Phalle',

artwork: obj1

