```js ModalDialog.js active

import { useEffect, useRef } from 'react';

export default function ModalDialog({ isOpen, children }) {

const ref = useRef();

useEffect(() => {

if (!isOpen) {

return;

}

const dialog = ref.current;

dialog.showModal();

return () => {

dialog.close();

};

}, [isOpen]);

return <dialog ref={ref}>{children}</dialog>;

}

