{type: 'deleted', id: 1},

{type: 'added', id: 3, text: 'Lennon Wall pic'},

];

let finalState = actions.reduce(tasksReducer, initialState);

const output = document.getElementById('output');

output.textContent = JSON.stringify(finalState, null, 2);

