```js Counter.js active

import { useState, useEffect } from 'react';

export default function Counter() {

const [count, setCount] = useState(0);

useEffect(() => {

function onTick() {

setCount(c => c + 1);

}

setInterval(onTick, 1000);

}, []);

return <h1>{count}</h1>;

}

