/>

<TaskList

tasks={tasks}

onChangeTask={handleChangeTask}

onDeleteTask={handleDeleteTask}

/>

</>

);

}

function tasksReducer(tasks, action) {

switch (action.type) {

case 'added': {

return [...tasks, {

id: action.id,

text: action.text,

done: false

