```js App.js

import { useState } from 'react';

export default function App() {

const [version, setVersion] = useState(0);

function handleReset() {

setVersion(version + 1);

}

return (

<>

<button onClick={handleReset}>Reset</button>

<Form key={version} />

</>

);

}

function Form() {

